html.script:
var myArray = [40,62,14,16,19,9, 32];



let svgContainer = d3.select("#myDiv").append("svg")
	.attr("width",1000)
	.attr("height",1000);

let circles = svgContainer.selectAll("circle")
	.data(myArray);

 // D3 enter method cycles through array like a for loop.
circles.enter()
	.append("circle")
		.attr("cx", function(d,i){
		return 50 + (i*50);
		}) //called an anomynous function
		.attr("cy",70)
		.attr("r",function(d){
		return d;
		}) //d refers to the data
		.attr("fill","blue")	


let myRectangle = svgContainer.selectAll("rect")
	.data(myArray);

myRectangle.enter()
	.append("rect")
		.attr("x", function(d,i){return 50 + (i*110);})
		.attr("y",500)
		.attr("width",100)
		.attr("height",function(d){return d;})
		.attr("fill","green")
